import React, { useState } from "react";
import styles from "./InvestForm.module.css";

export default function InvestForm() {
  const [savings, setSavings] = useState("");
  const [yearlySaving, setYearlySavings] = useState("");
  const [invInterest, setInvInterest] = useState("");
  const [invDuration, setInvDuration] = useState("");


  const currentSavingsChangeHandler = (e) => {
    setSavings(e.target.value);
  }
  const yealrySavingChangeHandler = (e) => {
    setYearlySavings(e.target.value); 
  }

  const invInterestChangeHandler = (e) => {
    setInvInterest(e.target.value); 
  }
  
  const invDurationChangeHandler = (e) => {
    setInvDuration(e.target.value);
  }


  const resetHandler = () => {
    setSavings("");
    setYearlySavings("");
    setInvInterest("");
    setInvDuration("");
  };
  const calculateHandler = (userInput) => {
    userInput.preventDefault();
    // Should be triggered when form is submitted
    // You might not directly want to bind it to the submit event on the form though...

    const yearlyData = []; // per-year results

    let currentSavings = +userInput["current-savings"]; // feel free to change the shape of this input object!
    const yearlyContribution = +userInput["yearly-contribution"]; // as mentioned: feel free to change the shape...
    const expectedReturn = +userInput["expected-return"] / 100;
    const duration = +userInput["duration"];

    // The below code calculates yearly results (total savings, interest etc)
    for (let i = 0; i < duration; i++) {
      const yearlyInterest = currentSavings * expectedReturn;
      currentSavings += yearlyInterest + yearlyContribution;
      yearlyData.push({
        // feel free to change the shape of the data pushed to the array!
        year: i + 1,
        yearlyInterest: yearlyInterest,
        savingsEndOfYear: currentSavings,
        yearlyContribution: yearlyContribution,
      });
    }
    console.log (yearlyData);
    setSavings("");
    setYearlySavings("");
    setInvInterest("");
    setInvDuration("");
    // do something with yearlyData ...
  };
  return (
    <div>
      <form className={styles.form}>
        <div className={styles["input-group"]}>
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
            <input type="number" id="current-savings" onChange ={currentSavingsChangeHandler}/>
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
            <input type="number" id="yearly-contribution" onChange = {yealrySavingChangeHandler} />
          </p>
        </div>
        <div className={styles["input-group"]}>
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input type="number" id="expected-return" onChange = {invInterestChangeHandler}/>
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input type="number" id="duration" onChange = {invDurationChangeHandler} />
          </p>
        </div>
        <p className={styles.actions}>
          <button
            type="reset"
            className={styles.buttonAlt}
            onClick={calculateHandler}
          >
            Reset
          </button>
          <button
            type="submit"
            className={styles.button}
            onClick={resetHandler}
          >
            Calculate
          </button>
        </p>
      </form>
    </div>
  );
}
